$.validator.setDefaults({
	highlight:function(e){
	$(e).closest(".form-group").removeClass("has-success").addClass("has-error")},
	success:function(e){e.closest(".form-group").removeClass("has-error").addClass("has-success")}
	,errorElement:"span"
	,errorPlacement:function(e,r){e.appendTo(r.is(":radio")||r.is(":checkbox")?r.parent().parent().parent():r.parent())}
	,errorClass:"help-block m-b-none",validClass:"help-block m-b-none"}),
	$().ready(function(){
		var e="<i class='fa fa-times-circle'></i> ";
		$("#edit").validate(
				{
					rules:{
						faccount:"required"
						,fusername:{required:!0,minlength:2}
						,fpassword:{required:!0,minlength:5}
						,confirm_password:{required:!0,minlength:5,equalTo:"#fpassword"}
					},
					messages:{
						faccount:e+"请输入帐号",
						fusername:{required:e+"请输入用户名",minlength:e+"用户名必须两个字符以上"},
						fpassword:{required:e+"请输入您的密码",minlength:e+"密码必须5个字符以上"},
						confirm_password:{required:e+"请再次输入密码",minlength:e+"密码必须5个字符以上",equalTo:e+"两次输入的密码不一致"}
					}
				})
						,$("#faccount").focus(
								function(){var e=$("#faccount").val()
								,r=$("#lastname").val();e&&r&&!this.value&&(this.value=e+"."+r)})});