mapper.update:
update
VERSION = #{version}+1 , updatetime=now(), fdel_flag=#{fdelFlag}, rgn_no=#{rgnNo}, mer_name=#{merName}, rgn_full=#{rgnFull}, parent_no=#{parentNo}, parent_name=#{parentName}, expanded_flag=#{expandedFlag}, leaf_flag=#{leafFlag},
ID = #{id} and VERSION = #{version}

mapper.sql:
and t.FDEL_FLAG = 0
and t.mer_name like  concat('%',#{queryKey},'%') 
 and t.rgn_no!='0' 
and t.rgn_no like concat(#{rgnNo},'%')
and t.rgn_no=#{singleRgnNo}
and t.mer_name=#{merName}
and t.rgn_full=#{rgnFull}
and t.parent_no=#{singleParentNo}
and t.parent_no=#{parentNo}
and t.parent_name=#{parentName}
and t.expanded_flag=#{expandedFlag}
and t.leaf_flag=#{leafFlag}
and t.his_no=#{hisNo}
